import UIKit

enum ListMovies {
  // MARK: Use cases
  
  enum FetchMovies {
    struct Request {
      var page: Int
    }
    struct Response {
      var movies: [MovieObject]
    }
    struct ViewModel {
      
      struct DisplayedMovie {
        var id: Int
        var title: String
        var averageScore: Double
        var backdropImageURL: URL
        var isFavorite: Bool
      }
      
      var displayedMovies: [DisplayedMovie]
    }
  }
  
  enum SearchMovies {
    struct Request {
      var query: String
    }
    
    struct Response {
      var movies: [MovieObject]
    }
    
    struct ViewModel {
      
      struct MovieResult {
        var id: Int
        var title: String
        var averageScore: Double
        var backdropImageURL: URL
      }
      
      var movieResults: [MovieResult]
    }
  }
}
